}

pub struct VersionedToolCall {
    /// Tool name
    pub tool: String,
    /// Input parameters
    pub input: serde_json::Value,
    /// Optional version pin (if not specified, uses latest)
    pub version_pin: Option<ToolVersionPin>,
    /// Error handling for version mismatch
    pub on_version_mismatch: VersionMismatchBehavior,
}

pub enum VersionMismatchBehavior {
    /// Fail the call if pinned version unavailable
    Fail,
    /// Warn and use latest
    WarnAndUseLast,
    /// Silently use latest
    UseLast,
}

impl ToolCache {
    /// Pin current version for session stability
    pub fn pin_version(&mut self, connector: &ConnectorId) -> ToolVersionPin {
        let current = self.get(connector).unwrap();
        ToolVersionPin {
            schema_version: current.schema_version.version,
            content_hash: current.schema_version.content_hash,
            pinned_at: Utc::now(),
            expires_at: Some(Utc::now() + chrono::Duration::hours(24)),
        }
    }

    /// Check if pinned version is still valid
    pub fn validate_pin(&self, connector: &ConnectorId, pin: &ToolVersionPin) -> PinValidation {
        match self.get(connector) {
            Some(current) => {
                if current.schema_version.content_hash == pin.content_hash {
                    PinValidation::Valid
                } else {
                    PinValidation::Changed {
                        breaking: self.is_breaking_change(pin, current),
                    }
                }
            }
            None => PinValidation::ConnectorRemoved,
        }
    }
}
